INSERT INTO autoresponders (`type`,`name`,subject,contents,contents_html,description,helpid) VALUES( 3, 'Draft Invoice Template', 'Billing Department (Draft Invoice)',"*** Draft - This is not a bill ***\n\r\n\rInvoice #[INVOICENUMBER]\r\n\r\nATTN: [CLIENTNAME],\r\n\r\nYou currently have a draft invoice in the amount of [BALANCEDUE] on your account with [COMPANYNAME].  \r\n\r\nInvoice Description:\r\n[INVOICEDESCRIPTION]\r\n\r\nTotal Amount: [AMOUNT]\r\n\r\nIf you have any questions regarding your account, please feel free to contact us.\r\n\r\n[COMPANYNAME]\r\n[BILLINGEMAIL]","<HTML><head></head><body>*** Draft - This is not a bill ***<br />\r\n<br />\r\nInvoice #[INVOICENUMBER]<br />\r\n<br />\r\nATTN: [CLIENTNAME],<br />\r\n<br />\r\nYou currently have a draft invoice in the amount of [BALANCEDUE] on your account with [COMPANYNAME].  <br />\r\n<br />\r\nInvoice Description:<br />\r\n[INVOICEDESCRIPTION]<br />\r\n<br />\r\nTotal Amount: [AMOUNT]<br />\r\n<br />\r\nIf you have any questions regarding your account,<br />\r\nplease feel free to contact us.<br />\r\n<br />\r\n[COMPANYNAME]<br />\r\n[BILLINGEMAIL]</body></HTML>",'Template for draft invoices sent through E-mail.',50);
