INSERT INTO `help` VALUES(68, 'Notify Package Suspension Tags', '[{"name":"CLIENTNAME","description":"Client''s first and last name","hostingonly":false},{"name":"FIRSTNAME","description":"Client''s First name","hostingonly":false},{"name":"LASTNAME","description":"Client''s Last name","hostingonly":false},{"name":"CLIENTEMAIL","description":"Client''s Email","hostingonly":false},{"name":"ORGANIZATION","description":"Client''s Organization","hostingonly":false},{"name":"CCLASTFOUR","description":"The last four digits of the customer''s credit card."},{"name":"CCEXPDATE","description":"The expiration date of the customer''s credit card."},{"name":"CLIENTAPPLICATIONURL","description":"Url to your ClientExec installation"},{"name":"FORGOTPASSWORDURL","description":"URL to retrieve forgotten password."},{"name":"BILLINGEMAIL","description":"Email address for billing inquiries"},{"name":"SUPPORTEMAIL","description":"E-mail to support staff"},{"name":"COMPANYNAME","description":"Company name"},{"name":"COMPANYADDRESS","description":"Company address"},{"name":"PACKAGEID","description":"Client''s Plan ID","hostingonly":false},{"name":"TICKETNUMBER","description":"Ticket Number"},{"name":"DATE","description":"Date payment is due."},{"name":"CUSTOMPROFILE_xxxx","description":"Custom Profile<br/><i style=''font-size:smaller;''>where xxx is a profile custom field name (i.e. [CUSTOMPROFILE_somename])</i>","hostingonly":false},{"name":"CUSTOMPACKAGE_xxxx","description":"Custom Package Field<br/><i style=''font-size:smaller;''>Where xxx is custom package field name (i.e. [CUSTOMPACKAGE_somename])</i>","hostingonly":false}]', 'Click to view available tags', 240, 320);
INSERT INTO autoresponders (`type`,`name`,subject,contents,description,helpid) VALUES( 3, 'Notify Package Suspension','Package has been suspended',"<HTML><head></head><body>ATTN: [CLIENTNAME],<br />\n<br />\nYour package [PACKAGENAME] has been suspended for lack of payment.<br>If you would like to activate it again, please contact us at [BILLINGEMAIL].<br />\n<br />\n<br />\n[COMPANYNAME]<br />\n[BILLINGEMAIL]</body></HTML>",'Template sent to clients whose package are suspended through the Autosuspend automation service.',68);
